catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var dateNeeded = g_form.getValue('date_needed');
   var dateToReturn = g_form.getValue('date_to_return_vehicle');
   //If date return is before date needed trigger an error message
   if(dateToReturn < dateNeeded) {
	g_form.addErrorMessage('Date to Return should not be before the Date needed');
	g_form.clearValue('date_to_return_vehicle');
	return false; //Prevent form submission
   }
   return true;// Allow Form submission
}
